import { useState } from "react"
import styles from './balance.module.css'

export function Balance() {

    const [bal, setBal] = useState(0)

    const onBalance = (e) => {

        e.preventDefault()

        const acId = e.target.acId.value
        
        console.log(`Id ${acId}`)

        fetch(`http://localhost:3000/balance/${acId}`)
        .then( res => res.json() )
        .then(json => setBal(json.bal))

    }

    return (

        <div className={styles.balCont}>
        <h1> Balance is: INT. {bal} </h1>
        <form on Submit = {onBalance}>
            <input type='number' placeHolder='Account Id' name='acId'/>
            <input type='submit' value='Balance'/>
        </form>
    </div>

    )
}
